import React from 'react';
import styled from 'styled-components';
import { Outlet, useNavigate } from 'react-router-dom';

const ConfirmLayout = () => {
  const navigate = useNavigate();
  return (
    <>
      <Header>
        <Logo onClick={() => navigate('/')} />
        <h2>예약 확인</h2>
        <ConfirmLink onClick={() => navigate('/')}>돌아가기</ConfirmLink>
      </Header>
      <Main>
        <Outlet />
      </Main>
    </>
  );
};

export default ConfirmLayout;

const Header = styled.header`
  width: 100%;
  max-width: 976px;
  height: 80px;
  line-height: 80px;
  margin: 0 auto;
  margin-bottom: 30px;
  ${({ theme }) => theme.mixins.flexBox('center', 'space-between')};
  h2 {
    font-size: 24px;
    font-weight: bold;
  }

  ${({ theme }) => theme.media.tablet`
		width: 480px;
	`}
  ${({ theme }) => theme.media.mobile`
		padding: 0 30px;
		width: 100%;
    margin-bottom: 20px;
		div, h2 {
			flex-shrink: 0;
		}
	`}
`;

const imgUrl =
  'data:image/svg+xml;base64,';

const Logo = styled.div`
  width: 125px;
  height: 31px;
  background: url(${imgUrl}) center center / contain no-repeat;
  cursor: pointer;
`;

const ConfirmLink = styled.div`
  width: 125px;
  font-size: 18px;
  text-align: center;
  cursor: pointer;
  ${({ theme }) => theme.media.mobile`
    width: auto;
	`}
`;

const Main = styled.main`
  width: 100%;
  max-width: 976px;
  height: calc(100% - 80px);
  margin: 0 auto;
  ${({ theme }) => theme.media.tablet`
		width: 480px;
	`}
  ${({ theme }) => theme.media.mobile`
		padding: 0 30px;
		width: 100%;
	`}
`;
